import { React, createRef } from "react";
import { useHistory } from "react-router-dom";
import Hourly from "./Hourly"
import Statistics from "./Statistics"

const Home = () => {
  let history = useHistory();
  let hourlyRef = createRef();
  let statiticsRef = createRef();
console.log(hourlyRef)
  let handleChange = (value) => {
    hourlyRef.style.display = "none"
    statiticsRef.style.display = "none"
    value.style.display = "block"
  }

  return (
    <div className="home-div">
      <select name="data-type" id="data-type-select" onChange={(event) => {handleChange(event.target.value)}}>
            <option value={this.hourlyRef}>Hourly</option>
            <option value={this.statiticsRef}>Statistics</option>
      </select>
      <h1>Welcome to Daily Weather News</h1>
      <Hourly style={{display: "none"}} ref={(div) => {hourlyRef = div}}/>
      <Statistics style={{display: "none"}} ref={(div) => {statiticsRef = div}}/>
    </div>
  );
};

export default Home;
