import React, { useState } from 'react';
import Hourly from "./dataTypes/Hourly.jsx"
import Statistics from "./dataTypes/Statistics"

const Home = () => {
  const [selectedComponent, setSelectedComponent] = useState();

  // let hourlyRef = React.createRef();
  // let statiticsRef = React.createRef();
  // let hourly = JSON.stringify(hourlyRef)
  // let statistics = JSON.stringify(statiticsRef)

  let handleChange = (value) => {
    // hourly.style.display = "none"
    // statistics.style.display = "none"
    // value.style.display = "block"
  }

  let renderSelectedComponent = (comp) => {
    if (comp.value = "Hourly") {
      return (
        <Hourly />
      )
      } else if (comp.value = "Statistics") {
        return (
          <Statistics />
        )
      }
  }

  return (
    <div className="home-div">
      <select name="data-type" id="data-type-select" onChange={(e) => {setSelectedComponent({ selectedComponent: e.target.value })}}>
            <option>Hourly</option>
            <option>Statistics</option>
      </select>
      <h1>Welcome to Daily Weather News</h1>
      {/* <Hourly ref={hourlyRef}/> */}
      {/* <Statistics ref={statiticsRef}/> */}
      {renderSelectedComponent(selectedComponent)}
    </div>
  );
};

export default Home;
