import React, { Component } from "react";
import { Link } from "react-router-dom";
// import NavLinkComponent from "./NavLinkComponent";

class NavLinks extends Component {
  constructor(props) {
    super(props);

    this.statisticsRef = React.createRef();
    this.hourlyRef = React.createRef();
    this.aboutRef = React.createRef();

    this.state = {
      navLinks: [
        {
          id: 1,
          name: "Statistics",
          path: "/statistics",
          linkRef: this.statisticsRef,
        },
        {
          id: 2,
          name: "Hourly",
          path: "/hourly",
          linkRef: this.hourlyRef,
        },
        {
          id: 3,
          name: "About",
          path: "/about",
          linkRef: this.aboutRef,
        },
      ],
    };
  }

  onActivateItem = (item) => {
    this.statisticsRef.classList.remove("active");
    this.hourlyRef.classList.remove("active");
    this.aboutRef.classList.remove("active");
    item.classList.add("active");
  };

  render() {
    return (
      <div className="nav-links">
        <ul className="navbar-nav mr-auto mt-lg-0">
          {this.state.navLinks.map((navLink, id) => (
            <NavLinkComponent
              {...navLink}
              key={id}
              activateItem={() => this.onActivateItem(navLink.linkRef)}
            />
          ))}
