import React, { useContext, createRef } from "react";
import { WeatherData } from "../utilities/WeatherData";

const Search = (props) => {
  let { searchCity, searchInputRef, searchBtnRef } = useContext(WeatherData);
  // let searchInputRef = createRef();
  return (
    <div className="searchFormDiv">
      <form className="form-inline my-2 my-lg-0">
        <input
          id="citySearchInput"
          className="form-control mr-sm-2"
          type="search"
          placeholder="Enter City Here"
          aria-label="City"
          ref={(input) => (searchInputRef = input)}
        />
        <button
          id="citySearchBtn"
          type="button"
          className="btn btn-outline-light my-2 my-sm-0"
          ref={(button) => (searchBtnRef = button)}
          onClick={() => searchCity(searchInputRef.value)}
        >
          Search
        </button>
      </form>
    </div>
  );
};

export default Search;
