import React, { useState, useEffect } from 'react';
import Hourly from "./Hourly"
import Statistics from "./Statistics"
import "./css/PageView.css"
import { WeatherData } from "../utilities/WeatherData"

const Home = () => {
  const [selectedComponent, setSelectedComponent] = useState("Hourly");

  // state = {
  //   selectedComponent: 'Hourly'
  // }

  // textTitle = () => {

  // }

  let renderSelectedComponent = (component) => {
   if (component.selectedComponent === "Hourly") {
     return <Hourly/>
   } else if (component.selectedComponent === "Statistics") {
     return <Statistics/>
   }
  }

  useEffect(() => {
    renderSelectedComponent(selectedComponent)
  })

  // componentDidMount() {
  //   this.renderSelectedComponent(this.state.selectedComponent)
  // }


  return (
    <div className="home-div">
      <h1>Welcome to Daily Weather News</h1>
      <select name="data-type" id="data-type-select" onChange={(e) => {setSelectedComponent({ selectedComponent: e.target.value })}}>
            <option>Hourly</option>
            <option>Statistics</option>
      </select>
      {renderSelectedComponent(selectedComponent)}
    </div>
  );
};

export default Home;
