import React, { Component } from "react";
import { Link } from "react-router-dom";

class NavLinks extends Component {
  constructor(props) {
    super(props);

    this.unorderedList = React.createRef();
    this.statisticsRef = React.createRef();
    this.hourlyRef = React.createRef();
    this.aboutRef = React.createRef();

    this.state = {
      navLinks: [
        {
          id: 1,
          name: "Statistics",
          path: "/statistics",
        },
        {
          id: 2,
          name: "Hourly",
          path: "/hourly",
        },
        {
          id: 3,
          name: "About",
          path: "/about",
        },
      ],
    };
  }

  onActivateItem = (item) => {
    this.statisticsRef.classList.remove("active");
    this.hourlyRef.classList.remove("active");
    this.aboutRef.classList.remove("active");
    item.classList.add("active");
  };

  render() {
    return (
      <div className="nav-links">
        <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
          {/* {this.state.navLinks.map((navLink, id) => (
            <NavLinkComponent {...navLink} key={id} />
          ))} */}
          <Link to={"/statistics"}>
            <li
              className="nav-item"
              ref={(li) => (this.statisticsRef = li)}
              onClick={() => this.onActivateItem(this.statisticsRef)}
            >
              Statistics
            </li>
          </Link>
          <Link to={"/hourly"}>
            <li
              className="nav-item"
              ref={(li) => (this.hourlyRef = li)}
              onClick={() => this.onActivateItem(this.hourlyRef)}
            >
              <a classNAme="nav">Hourly</a>
            </li>
          </Link>
          <Link to={"/about"}>
            <li
              className="nav-item"
              ref={(li) => (this.aboutRef = li)}
              onClick={() => this.onActivateItem(this.aboutRef)}
            >
              About
            </li>
          </Link>
        </ul>
      </div>
    );
  }
}

export default NavLinks;
