import React, { Component } from "react";
import logo from "../img/logo.svg";
import "./css/Nav.css";

import NavLinks from "./NavLinks";
import Search from "./Search";
import { WeatherData } from "../WeatherData";

// const Nav = (props) => {
// let collapseNavRef = React.createRef();
// let searchInputRef = React.createRef();

// const onToglerClick = () => {
//   collapseNavRef.classList.add("show");
// };

class Nav extends Component {
  static contextType = WeatherData;
  render() {
    console.log(this.context);
    return (
      <div className="navbar navbar-expand-lg navbar-dark bg-primary">
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarTogglerDemo01"
          aria-controls="navbarTogglerDemo01"
          aria-expanded="false"
          aria-label="Toggle navigation"
          // onClick={onToglerClick}
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div
          className="collapse navbar-collapse"
          id="navbarTogglerDemo01"
          ref={(div) => (collapseNavRef = div)}
        >
          <a className="navbar-brand" href="/statistics">
            <img src={logo} width="30" height="30" alt="cloudy sun" />
          </a>
          <NavLinks />
        </div>
        <Search />
      </div>
    );
  }
}

export default Nav;
