import React, { Component, createRef } from "react";
import logo from "../img/logo.svg";
import "./css/Nav.css";

import NavLinks from "./NavLinks";
import Search from "./Search";
import { WeatherData } from "../WeatherData";

// const Nav = (props) => {
// let collapseNavRef = React.createRef();
// let searchInputRef = React.createRef();

// const onToglerClick = () => {
//   collapseNavRef.classList.add("show");
// };

class Nav extends Component {
  static contextType = WeatherData;
  constructor(props) {
    super(props);

    this.state = {
      helper: false,
    };
  }

  collapseNavRef = createRef();

  onToglerClick = () => {
    if (this.helper) {
      this.collapseNavRef.classList.add("show");
      this.setState({
        helper: false,
      });
    } else {
      this.collapseNavRef.classList.remove("show");
      this.setState({
        helper: true,
      });
    }
    console.log(this.state.helper);
  };

  render() {
    return (
      <div className="navbar navbar-expand-lg navbar-dark ">
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarTogglerDemo01"
          aria-controls="navbarTogglerDemo01"
          aria-expanded="false"
          aria-label="Toggle navigation"
          onClick={this.onToglerClick}
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div
          className="collapse navbar-collapse"
          id="navbarTogglerDemo01"
          ref={(div) => (this.collapseNavRef = div)}
        >
          <a className="navbar-brand" href="/statistics">
            <img src={logo} width="30" height="30" alt="cloudy sun" />
          </a>
          <NavLinks />
        </div>
        <Search />
      </div>
    );
  }
}

export default Nav;
