import React, { createRef } from "react";

const HourlyTableRow = (props) => {
  const hourlyRowRef = createRef();

  let hourlyRowBackground = (description) => {
    switch (description) {
      case "clear sky":
        return { backgroundImage: "url(../../img/clear_sky.jpg)" };
      case "overcast clouds":
      case "broken clouds":
        return { backgroundImage: "url(../../img/overcast_clouds.jpg)" };
      case "few clouds":
      case "scattered clouds":
        return { backgroundImage: "url(../../img/few_clouds.jpg)" };
      case "ligth rain":
      case "moderate rain":
        return { backgroundImage: "url(../../img/rain.jpg)" };
      case "snow":
        return { backgroundImage: "url(../../img/snow.jpg)" };
      default:
        break;
    }
  };

  return (
    <div className="row data-row" ref={(div) => (hourlyRowRef = div)}>
      <div className="col-md-2">{props.weather[0].description}</div>
      <div className="col-md-2">
        {props.dt_txt.split(" ")[1]}
        {/* {new Date(props.dt * 1000).toUTCString()} */}
      </div>
      <div className="col-md-2">
        {props.main.temp.toFixed(0)} °C ({props.main.feels_like.toFixed(0)} °C)
      </div>
      <div className="col-md-2">{props.main.humidity} %</div>
      <div className="col-md-2">{props.wind.speed} km/h</div>
    </div>
  );
};

export default HourlyTableRow;
