import React, { useContext, useState, useEffect } from "react";
import "./css/PageView.css";
import { WeatherData } from "../utilities/WeatherData";

const LoadedStats = () => {
  const { calculatedStats } = useContext(WeatherData);
  console.log(calculatedStats);
  return (
    <div className="statisticsResult">
      <div className="row">
        <div className="col-md-6">
          MAX TEMP: ${calculatedStats.temperature.max} C
        </div>
        <div className="col-md-6">
          MAX HUMD: ${calculatedStats.humidity.max} %
        </div>
      </div>
      <div className="row">
        <div className="col-md-6">
          AVG TEMP: ${calculatedStats.temperature.avg.toFixed(2)} C
        </div>
        <div className="col-md-6">
          AVG HUMD: ${calculatedStats.humidity.avg.toFixed(2)} %
        </div>
      </div>
      <div className="row">
        <div className="col-md-6">
          MIN TEMP: ${calculatedStats.temperature.min} C
        </div>
        <div className="col-md-6">
          MIN HUMD: ${calculatedStats.humidity.min} %
        </div>
      </div>
      <h3>
        Warmest time of the following period: ${calculatedStats.warmestTime}
      </h3>
      <h3>
        Coldest time of the following period: ${calculatedStats.coldestTime}
      </h3>
    </div>
  );
};

export default LoadedStats;
