import React, { useContext } from "react";
import { WeatherData } from "../utilities/WeatherData";
import HourlyTableRow from "./HourlyTabelRow";

const Hourly = () => {
  const { weatherData } = useContext(WeatherData);

  return (
    <div className="hourly-div">
      <h1>Hourly Weather Report</h1>
      <p>
        {" "}
        <i>
          Here you can read the hourly weather report for the following days.
          Keep in mind that it is not actually every hour, but every 3 - 5 hours
        </i>{" "}
      </p>
      {/* {weatherData.list && ( */}
      <div className="hourly-result">
        <div className="row">
          <div className="col-md-2">Description</div>
          <div className="col-md-2">Time</div>
          <div className="col-md-2">Temperature (Real Feel)</div>
          <div className="col-md-2">Humidity</div>
          <div className="col-md-2">Wind Speed</div>
        </div>
        {/* {weatherData.list.map((item, id) => (
            <HourlyTableRow {...item} key={id} />
          ))} */}
      </div>
      {/* )} */}

      <div
        className="row data-row"
        style={hourlyRowBackground(props.weather[0].description)}
      >
        <div className="col-md-2 hourly-desc">
          {props.weather[0].description}
        </div>
        <div className="col-md-2">
          {props.dt_txt.split(" ")[1]}
          {/* {new Date(props.dt * 1000).toUTCString()} */}
        </div>
        <div className="col-md-2">
          {props.main.temp.toFixed(0)} °C ({props.main.feels_like.toFixed(0)}{" "}
          °C)
        </div>
        <div className="col-md-2">{props.main.humidity} %</div>
        <div className="col-md-2">{props.wind.speed} km/h</div>
      </div>

      <div
        className="row data-row"
        style={hourlyRowBackground(props.weather[0].description)}
      >
        <div className="col-md-2 hourly-desc">
          {props.weather[0].description}
        </div>
        <div className="col-md-2">
          {props.dt_txt.split(" ")[1]}
          {/* {new Date(props.dt * 1000).toUTCString()} */}
        </div>
        <div className="col-md-2">
          {props.main.temp.toFixed(0)} °C ({props.main.feels_like.toFixed(0)}{" "}
          °C)
        </div>
        <div className="col-md-2">{props.main.humidity} %</div>
        <div className="col-md-2">{props.wind.speed} km/h</div>
      </div>
    </div>
  );
};

export default Hourly;
