import React, { Component } from "react";
import NavLinkComponent from "./NavLinkComponent";
import { Link } from "react-router-dom";

class NavLinks extends Component {
  constructor(props) {
    super(props);

    this.unorderedList = React.createRef();
    this.statisticsRef = React.createRef();
    this.hourlyRef = React.createRef();
    this.aboutRef = React.createRef();

    this.state = {
      navLinks: [
        {
          id: 1,
          name: "Statistics",
          path: "/statistics",
        },
        {
          id: 2,
          name: "Hourly",
          path: "/hourly",
        },
        {
          id: 3,
          name: "About",
          path: "/about",
        },
      ],
    };
  }

  //   onClick = (item) => {
  //     this.state.navItems.forEach((navItem) => {
  //       navItem.classList.remove("active");
  //     });
  //     item.classList.add("active");
  //   };

  //   onClick = () => {
  //     console.log(this.statistics);
  //   };

  render() {
    return (
      <div className="nav-links">
        <ul
          ref={this.unorderedList}
          className="navbar-nav mr-auto mt-2 mt-lg-0"
        >
          {/* {this.state.navLinks.map((navLink, id) => (
            <NavLinkComponent {...navLink} key={id} />
          ))} */}
          <Link to={"/statistics"}>
            <li
              className="nav-link"
              ref={this.hourly}
              onClick={this.props.onActivateItem}
            >
              Statistics
            </li>
          </Link>
          <Link to={"/hourly"}>
            <li className="nav-link" ref={(li) => (this.hourly = li)}>
              Hourly
            </li>
          </Link>
          <Link to={"/about"}>
            <li className="nav-link" ref={(li) => (this.about = li)}>
              About
            </li>
          </Link>
        </ul>
      </div>
    );
  }
}

export default NavLinks;
