import React from "react";
import { WeatherData } from "../WeatherData";
import calculateStats from "../calculateStats";

const Statistics = () => {
  const { weatherData } = React.useContext(WeatherData);
  const statistics = calculateStats(weatherData);
  return (
    <div className="statistics-div">
      <h1>WeatherAlert</h1>
      <div id="statisticsResult">
        <div className="row">
          <div className="col-md-6">MAX TEMP: C</div>
          <div className="col-md-6">MAX HUMD: %</div>
        </div>
        <div className="row">
          <div className="col-md-6">AVG TEMP: C</div>
          <div className="col-md-6">AVG HUMD: %</div>
        </div>
        <div className="row">
          <div className="col-md-6">MIN TEMP: C</div>
          <div className="col-md-6">MIN HUMD: %</div>
        </div>
        <h3>Warmest time of the following period: </h3>
        <h3>Coldest time of the following period: </h3>
      </div>
    </div>
  );
};

export default Statistics;
