import React, {Component} from 'react';
import Hourly from "./Hourly"
import Statistics from "./Statistics"

class Home extends Component {
  constructor(props) {
    super(props)

    this.hourlyRef = React.createRef();
    this.statiticsRef = React.createRef();
  }
  // const hourlyRef = React.createRef();
  // const statiticsRef = React.createRef();

  // const handleChange = (value) => {
  //   hourlyRef.style.display = "none"
  //   statiticsRef.style.display = "none"
  //   value.style.display = "block"
  // }

  handleChange = (value) => {
    this.hourlyRef.style.display = "none"
    this.this.statiticsRef.style.display = "none"
    value.style.display = "block"
  }

  render() {
  return (
    <div className="home-div">
      <select name="data-type" id="data-type-select" onChange={(event) => {this.handleChange(event.target.value)}}>
            <option>Hourly</option>
            <option>Statistics</option>
      </select>
      <h1>Welcome to Daily Weather News</h1>
      <Hourly ref={this.hourlyRef}/>
      <Statistics ref={this.statiticsRef}/>
    </div>
  );
  }
};

export default Home;
