import React from 'react';
import Hourly from "./Hourly"
import Statistics from "./Statistics"

const Home = () => {
  let hourlyRef = React.createRef();
  let statiticsRef = React.createRef();

  let handleChange = (value) => {
    hourlyRef.current.style.display = "none"
    statiticsRef.current.style.display = "none"
    value.style.display = "block"
  }

  return (
    <div className="home-div">
      <select name="data-type" id="data-type-select" onChange={(event) => {handleChange(event.target.value)
      console.log(event.target.value, statiticsRef)}}>
            <option value={hourlyRef}>Hourly</option>
            <option value={statiticsRef}>Statistics</option>
      </select>
      <h1>Welcome to Daily Weather News</h1>
      <Hourly ref={hourlyRef}/>
      <Statistics ref={statiticsRef}/>
    </div>
  );
};

export default Home;
