import React, { Component } from "react";
// import NavLinkComponent from "./NavLinkComponent";

class NavLinks extends Component {
  constructor(props) {
    super(props);

    this.state = {
      navLinks: [
        {
          id: 1,
          name: "Statistics",
          path: "/statistics",
        },
        {
          id: 2,
          name: "Hourly",
          path: "/hourly",
        },
        {
          id: 3,
          name: "About",
          path: "/about",
        },
      ],
      navItems: [this.statistics, this.hourly, this.about],
    };
  }

  render() {
    onClick = (item) => {
      for (navItem of this.state.navItems) {
        navItem.classList.remove("active");
      }
      item.classList.add("active");
    };

    return (
      <div className="nav-links">
        <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
          {/* {this.state.navLinks.map((navLink, id) => (
            <NavLinkComponent {...navLink} key={id} />
          ))} */}
          <Link to={"/statistics"}>
            <li className="nav-link" ref={(li) => (this.statistics = li)}>
              Statistics
            </li>
          </Link>
          <Link to={"/hourly"}>
            <li className="nav-link" ref={(li) => (this.hourly = li)}>
              Hourly
            </li>
          </Link>
          <Link to={"/about"}>
            <li className="nav-link" ref={(li) => (this.about = li)}>
              About
            </li>
          </Link>
        </ul>
      </div>
    );
  }
}

export default NavLinks;
