import React, { createContext, Component } from "react";
import fetchData from "./utils";

export const WeatherData = createContext();

class WeatherDataProvider extends Component {
  //   constructor(props) {
  //     super(props);

  state = {
    weatherData: {},
  };
  //   }

  async componentDidMount() {
    let data = await fetchData(
      `https://api.openweathermap.org/data/2.5/forecast?q=Skopje&units=metric&APPID=a2b4e52032d2ff195e2ec1133387d853`
    );
    this.setState({
      weatherData: data,
    });
  }

  render() {
    return (
      <WeatherData.Provider value={{ ...this.state }}>
        {this.props.children}
      </WeatherData.Provider>
    );
  }
}

export default WeatherDataProvider;
