import React, { useContext } from "react";
import "./css/PageView.css";
import { WeatherData } from "../utilities/WeatherData";

const Statistics = () => {
  const { calculatedStats } = useContext(WeatherData);
  console.log(calculatedStats);
  const { temperature, humidity, warmestTime, coldestTime } = calculatedStats;
  return (
    <div className="stats-div">
      <h1>Statistics of the day</h1>
      <p>
        <i>Please enter a city in the Search bar to see the Daily Statistics</i>
      </p>
      {temperature && (
        <div className="stats-result">
          <div className="row">
            {temperature && (
              <div className="col-md-6">MAX TEMP: {temperature.max} °C</div>
            )}
            {humidity && (
              <div className="col-md-6">MAX HUMD: {humidity.max} %</div>
            )}
          </div>
          <div className="row">
            {temperature && (
              <div className="col-md-6">
                AVG TEMP: {temperature.avg.toFixed(2)} °C
              </div>
            )}
            {humidity && (
              <div className="col-md-6">
                AVG HUMD: {humidity.avg.toFixed(2)} %
              </div>
            )}
          </div>
          <div className="row">
            {temperature && (
              <div className="col-md-6">MIN TEMP: {temperature.min} °C</div>
            )}
            {humidity && (
              <div className="col-md-6">MIN HUMD: {humidity.min} %</div>
            )}
          </div>
          {warmestTime && (
            <h3 className="warmest-period">
              Warmest time of the following period: {warmestTime}
            </h3>
          )}
          {coldestTime && (
            <h3 className="coldest-period">
              Coldest time of the following period: {coldestTime}
            </h3>
          )}

          {/* <div className="row">
          <div className="col-md-6">Highest Temperature: 25 °C</div>
          <div className="col-md-6">Highest Humidity: 56%</div>
        </div>
        <div className="row">
          <div className="col-md-6">Average Temperature: 19 °C</div>
          <div className="col-md-6">Average Humidity: 40%</div>
        </div>
        <div className="row">
          <div className="col-md-6">Lowest Temperature: 13 °C</div>
          <div className="col-md-6">Lowest Humidity: 24 %</div>
        </div>
        <h3 className="warmest-period">
          Warmest time of the following period: 13:00
        </h3>
        <h3 className="coldest-period">
          Coldest time of the following period: 05:00
        </h3> */}
        </div>
      )}
    </div>
  );
};

export default Statistics;
