import React, { createRef } from "react";

const HourlyTableRow = (props) => {
  let hourlyRowRef = createRef();

  let hourlyRowBackground = (description) => {
    switch (description) {
      case "clear sky":
        return {
          backgroundImage: "url(/static/media/clear_sky.cef496f2.jpg)",
        };
      case "overcast clouds":
        return {
          backgroundImage: "url(/static/media/overcast_clouds.28d0d36c.jpg)",
        };
      case "few clouds":
        return {
          backgroundImage: "url(/static/media/few_clouds.f812812e.jpg)",
        };
      case "broken clouds":
      case "scattered clouds":
        return {
          backgroundImage: "url(/static/media/broken_clouds.711e500a.jpg)",
        };
      case "light rain":
      case "moderate rain":
        return { backgroundImage: "url(/static/media/rain.dcb73b6a.jpg)" };
      case "snow":
        return { backgroundImage: "url(/static/media/snow.51e08e6d.jpg)" };
      default:
        break;
    }
  };

  return (
    <div
      className="row data-row"
      ref={(div) => (hourlyRowRef = div)}
      style={hourlyRowBackground(props.weather[0].description)}
    >
      <div className="col-md-2">{props.weather[0].description}</div>
      <div className="col-md-2">
        {props.dt_txt.split(" ")[1]}
        {/* {new Date(props.dt * 1000).toUTCString()} */}
      </div>
      <div className="col-md-2">
        {props.main.temp.toFixed(0)} °C ({props.main.feels_like.toFixed(0)} °C)
      </div>
      <div className="col-md-2">{props.main.humidity} %</div>
      <div className="col-md-2">{props.wind.speed} km/h</div>
    </div>
  );
};

export default HourlyTableRow;
